import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-payment-modal',
  templateUrl: './payment-modal.component.html',
  styleUrls: ['./payment-modal.component.css']
})
export class PaymentModalComponent {
  @Output() close = new EventEmitter<void>();
  @Output() payConfirmed = new EventEmitter<void>();

  selectedMethod: string = '';

  closeModal() {
    this.close.emit();
  }

  pay() {
    if (this.selectedMethod) {
      this.payConfirmed.emit();
      alert('Compra realizada con exito!');
      this.closeModal();
    } else {
      alert('Por favor, seleccione un método de pago.');
    }
  }

  selectPaymentMethod(method: string) {
    this.selectedMethod = method;
    console.log(`Selected payment method: ${method}`);
  }
}
<!-- Modal Background -->
<div class="modal-backdrop" (click)="closeModal()"></div>

<!-- Modal Container -->
<div class="modal-container">
  <div class="modal-header">
    <h2>Seleccione el Método de Pago</h2>
    <button class="close-btn" (click)="closeModal()">×</button>
  </div>

  <div class="modal-body">
    <form>
      <div class="payment-methods">
        <label>
          <input type="radio" name="paymentMethod" value="creditCard" (change)="selectPaymentMethod('Credit Card')"/>
          Tarjeta de Crédito
        </label>
        <label>
          <input type="radio" name="paymentMethod" value="paypal" (change)="selectPaymentMethod('PayPal')"/>
          PayPal
        </label>
        <label>
          <input type="radio" name="paymentMethod" value="bankTransfer" (change)="selectPaymentMethod('Bank Transfer')"/>
          Transferencia Bancaria
        </label>
      </div>
    </form>
  </div>

  <div class="modal-footer">
    <button class="pay-btn" (click)="pay()">Pagar</button>
    <button class="cancel-btn" (click)="closeModal()">Cancelar</button>
  </div>
</div>
