import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  title="Angular 2023"
  modificado = false

  cambiarTitle(){
    let titulo =""
    this.title = "Angular 2023-3"
    this.modificado = true
  }
  habilitar(){
    this.modificado = false
  }
}
<!-- Expresiones / One Way binding -->
<p>{{ title }}</p>
<p>{{ modificado }}</p>
<!-- directivas de atributo [] / Eventos () / One Way binding -->
<button [disabled]="modificado" (click)="cambiarTitle()">Cambiar Title</button>
<!-- ngModel / Two Way binding -->
<input type="text" [(ngModel)]="title" />

<button (click)="habilitar()">Habilitar</button>
