import { Component, OnInit } from '@angular/core';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-carrito',
  templateUrl: './carrito.component.html',
  styleUrls: ['./carrito.component.css']
})
export class CarritoComponent implements OnInit {
  cartItems: any[] = [];
  summary: { numberOfProducts: number, totalPrice: number } = { numberOfProducts: 0, totalPrice: 0 };
  shippingCost: number = Math.floor(Math.random() * 2000) + 2000; // Random number > 2000
  totalAmount: number = 0;

  constructor(private cartService: CartService) {}

  ngOnInit(): void {
    this.cartItems = this.cartService.getCart();
    this.summary = this.cartService.getCartSummary();
    this.totalAmount = this.summary.totalPrice + this.shippingCost;
  }

  updateQuantity(item: any): void {
    // Recalculate the total amount when quantity is updated
    this.summary = this.cartService.getCartSummary();
    this.totalAmount = this.summary.totalPrice + this.shippingCost;
  }
}
