import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-payment-modal',
  templateUrl: './payment-modal.component.html',
  styleUrls: ['./payment-modal.component.css']
})
export class PaymentModalComponent {
  @Output() close = new EventEmitter<void>();
  @Output() payConfirmed = new EventEmitter<void>();

  closeModal() {
    this.close.emit();
  }

  pay() {
    this.payConfirmed.emit();
  }

  selectPaymentMethod(method: string) {
    console.log(`Selected payment method: ${method}`);
    // Handle the selected payment method if needed
  }

  onBackdropClick(event: MouseEvent) {
    // Only close the modal if the click is on the backdrop, not the modal content
    if ((event.target as HTMLElement).classList.contains('modal-overlay')) {
      this.closeModal();
    }
  }
}
<div class="modal-backdrop" (click)="closeModal()"></div>
<div class="modal-content">
  <h2>Seleccionar Método de Pago</h2>
  <!-- Add your payment method options here -->
  <div class="payment-methods">
    <!-- Example payment method options -->
    <button (click)="selectPaymentMethod('Credit Card')">Tarjeta de Crédito</button>
    <button (click)="selectPaymentMethod('PayPal')">PayPal</button>
  </div>
  <div class="modal-buttons">
    <button (click)="pay()">Pagar</button>
    <button (click)="closeModal()">Cancelar</button>
  </div>
</div>
